"use client";

import * as AccordionPrimitive from "@radix-ui/react-accordion";
import { ChevronDown } from "lucide-react";
import { cn } from "@/lib/utils";

export interface AccordionGradientItem {
  id: string;
  title: string;
  content: string;
}

interface AccordionGradientProps {
  items: AccordionGradientItem[];
  defaultValue?: string;
  className?: string;
}

export default function AccordionGradient({
  items,
  defaultValue,
  className,
}: AccordionGradientProps) {
  return (
    <AccordionPrimitive.Root
      type="single"
      collapsible
      defaultValue={defaultValue}
      className={cn("space-y-3", className)}
    >
      {items.map((item) => (
        <AccordionPrimitive.Item
          key={item.id}
          value={item.id}
          className="rounded-xl overflow-hidden border border-border/50 bg-gradient-to-r from-background to-muted/30 data-[state=open]:from-primary/5 data-[state=open]:to-primary/10 transition-all duration-300"
        >
          <AccordionPrimitive.Header className="flex">
            <AccordionPrimitive.Trigger
              className={cn(
                "flex flex-1 items-center justify-between px-5 py-4 text-left text-[15px] font-medium transition-all",
                "[&[data-state=open]>svg]:rotate-180",
              )}
            >
              {item.title}
              <ChevronDown className="h-4 w-4 shrink-0 text-muted-foreground transition-transform duration-300" />
            </AccordionPrimitive.Trigger>
          </AccordionPrimitive.Header>
          <AccordionPrimitive.Content className="overflow-hidden text-sm data-[state=closed]:animate-accordion-up data-[state=open]:animate-accordion-down">
            <div className="px-5 pb-4 text-muted-foreground">
              {item.content}
            </div>
          </AccordionPrimitive.Content>
        </AccordionPrimitive.Item>
      ))}
    </AccordionPrimitive.Root>
  );
}
