"use client";

import * as AccordionPrimitive from "@radix-ui/react-accordion";
import { ChevronDown } from "lucide-react";
import { cn } from "@/lib/utils";

export interface AccordionHighlightItem {
  id: string;
  title: string;
  content: string;
}

interface AccordionHighlightProps {
  items: AccordionHighlightItem[];
  defaultValue?: string;
  className?: string;
}

export default function AccordionHighlight({
  items,
  defaultValue,
  className,
}: AccordionHighlightProps) {
  return (
    <AccordionPrimitive.Root
      type="single"
      collapsible
      defaultValue={defaultValue}
      className={cn("space-y-2", className)}
    >
      {items.map((item) => (
        <AccordionPrimitive.Item
          key={item.id}
          value={item.id}
          className="group rounded-lg border border-transparent bg-muted/40 data-[state=open]:border-primary data-[state=open]:bg-primary/5 transition-all duration-200"
        >
          <AccordionPrimitive.Header className="flex">
            <AccordionPrimitive.Trigger
              className={cn(
                "flex flex-1 items-center justify-between px-4 py-3 text-left text-[15px] font-medium transition-all",
                "[&[data-state=open]>svg]:rotate-180",
              )}
            >
              <span className="group-data-[state=open]:text-primary transition-colors">
                {item.title}
              </span>
              <ChevronDown className="h-4 w-4 shrink-0 text-muted-foreground group-data-[state=open]:text-primary transition-all duration-200" />
            </AccordionPrimitive.Trigger>
          </AccordionPrimitive.Header>
          <AccordionPrimitive.Content className="overflow-hidden text-sm data-[state=closed]:animate-accordion-up data-[state=open]:animate-accordion-down">
            <div className="px-4 pb-3 text-muted-foreground">
              {item.content}
            </div>
          </AccordionPrimitive.Content>
        </AccordionPrimitive.Item>
      ))}
    </AccordionPrimitive.Root>
  );
}
