"use client";

import * as React from "react";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import { cn } from "@/lib/utils";
import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
  horizontalListSortingStrategy,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

export interface DragTabItem {
  value: string;
  label: string;
  content?: React.ReactNode;
}

interface DragAndDropTabsProps {
  items?: DragTabItem[];
  defaultValue?: string;
  className?: string;
}

export default function DragAndDropTabs({
  items: defaultItems = [
    { value: "tab1", label: "Tab 1", content: "Content 1" },
    { value: "tab2", label: "Tab 2", content: "Content 2" },
    { value: "tab3", label: "Tab 3", content: "Content 3" },
    { value: "tab4", label: "Tab 4", content: "Content 4" },
  ],
  defaultValue,
  className,
}: DragAndDropTabsProps) {
  const [items, setItems] = React.useState(defaultItems);
  const [active, setActive] = React.useState(
    defaultValue || defaultItems[0].value,
  );

  const sensors = useSensors(useSensor(PointerSensor));

  function SortableTab({
    id,
    label,
    active,
  }: {
    id: string;
    label: string;
    active: boolean;
  }) {
    const {
      attributes,
      listeners,
      setNodeRef,
      transform,
      transition,
      isDragging,
    } = useSortable({ id });
    const style = {
      transform: CSS.Transform.toString(transform),
      transition,
      zIndex: isDragging ? 10 : "auto",
    };

    return (
      <TabsTrigger
        ref={setNodeRef}
        value={id}
        {...attributes}
        {...listeners}
        className={cn(
          "px-4 py-2 rounded-md text-sm font-medium flex items-center border justify-center cursor-pointer select-none transition-colors",
          active
            ? "bg-primary text-white shadow-md dark:bg-primary/80 dark:text-black"
            : "bg-background/50 text-foreground/20 dark:bg-background/30 dark:text-foreground/50 ",
          isDragging && "opacity-70 ring-2 ring-primary",
        )}
        style={style}
      >
        {label}
      </TabsTrigger>
    );
  }

  const handleDragEnd = (event: any) => {
    const { active: dragged, over } = event;
    if (dragged.id !== over?.id) {
      const oldIndex = items.findIndex((i) => i.value === dragged.id);
      const newIndex = items.findIndex((i) => i.value === over.id);
      setItems(arrayMove(items, oldIndex, newIndex));
    }
  };

  return (
    <div
      className={cn(
        "flex flex-col items-center justify-center min-h-[70vh] w-full",
        className,
      )}
    >
      <Tabs
        value={active}
        onValueChange={setActive}
        className="w-full max-w-lg"
      >
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
          <SortableContext
            items={items.map((i) => i.value)}
            strategy={horizontalListSortingStrategy}
          >
            <TabsList className="flex gap-2 bg-background/30 p-2 rounded-xl">
              {items.map((item) => (
                <SortableTab
                  key={item.value}
                  id={item.value}
                  label={item.label}
                  active={active === item.value}
                />
              ))}
            </TabsList>
          </SortableContext>
        </DndContext>

        <div className="mt-4 w-full max-w-lg">
          {items.map((item) => (
            <TabsContent key={item.value} value={item.value}>
              <div className="p-4 bg-card">{item.content}</div>
            </TabsContent>
          ))}
        </div>
      </Tabs>
    </div>
  );
}

"use client";

import * as React from "react";
import { Tabs as TabsPrimitive } from "@base-ui/react/tabs";

import { cn } from "@/lib/utils";

function Tabs({
  className,
  ...props
}: React.ComponentProps<typeof TabsPrimitive.Root>) {
  return (
    <TabsPrimitive.Root
      data-slot="tabs"
      className={cn("flex flex-col gap-2", className)}
      {...props}
    />
  );
}

function TabsList({
  className,
  ...props
}: React.ComponentProps<typeof TabsPrimitive.List>) {
  return (
    <TabsPrimitive.List
      data-slot="tabs-list"
      className={cn(
        "bg-muted text-muted-foreground inline-flex h-9 items-center justify-center rounded-lg p-[3px]",
        className,
      )}
      {...props}
    />
  );
}

function TabsTrigger({
  className,
  ...props
}: React.ComponentProps<typeof TabsPrimitive.Tab>) {
  return (
    <TabsPrimitive.Tab
      data-slot="tabs-trigger"
      className={cn(
        "data-[active]:bg-background dark:data-[active]:text-foreground focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:outline-ring text-foreground dark:text-muted-foreground inline-flex h-[calc(100%-1px)] items-center justify-center gap-1.5 rounded-md border border-transparent px-2 py-1 text-sm font-medium whitespace-nowrap transition-[color,box-shadow] focus-visible:ring-[3px] focus-visible:outline-1 disabled:pointer-events-none disabled:opacity-50 data-[active]:shadow-sm [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4 ring-offset-background focus-visible:outline-none focus-visible:ring-offset-2 data-[active]:text-foreground cursor-pointer",
        className,
      )}
      {...props}
    />
  );
}

function TabsContent({
  className,
  ...props
}: React.ComponentProps<typeof TabsPrimitive.Panel>) {
  return (
    <TabsPrimitive.Panel
      data-slot="tabs-content"
      className={cn("flex-1 outline-none", className)}
      {...props}
    />
  );
}

export { Tabs, TabsList, TabsTrigger, TabsContent };
