import React from "react";
import { Badge } from "@/components/ui/badge";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import { DownloadCloud, Loader2, Check } from "lucide-react";

/**
 * IconLabelSubtextButton
 *
 * A compact, accessible, and highly re-usable button for actions that
 * need an icon, a strong primary label and a smaller contextual subtext.
 * Built with shadcn/ui primitives and Tailwind. Includes built-in
 * loading & success states, an optional badge, and an optional tooltip.
 */

type Variant = "default" | "ghost" | "outline";
type Size = "sm" | "md" | "lg";

export interface IconLabelSubtextButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  icon?: React.ReactNode; // preferred: lucide-react icon
  label: string;
  subtext?: string;
  badge?: string | number; // small badge shown top-right
  tooltip?: string; // optional tooltip content
  variant?: Variant;
  size?: Size;
  loading?: boolean;
  success?: boolean; // briefly show success icon instead of provided icon
}

function sizeClasses(size: Size) {
  switch (size) {
    case "sm":
      return {
        padding: "px-3 py-2",
        icon: "w-4 h-4",
        label: "text-sm",
        subtext: "text-xs",
      };
    case "lg":
      return {
        padding: "px-5 py-3",
        icon: "w-6 h-6",
        label: "text-base",
        subtext: "text-sm",
      };
    case "md":
    default:
      return {
        padding: "px-4 py-2.5",
        icon: "w-5 h-5",
        label: "text-sm font-medium",
        subtext: "text-xs",
      };
  }
}

function variantClasses(variant: Variant) {
  switch (variant) {
    case "ghost":
      return "bg-transparent hover:bg-muted/50 border-transparent";
    case "outline":
      return "bg-transparent border border-border hover:bg-muted";
    case "default":
    default:
      return "bg-primary text-primary-foreground hover:bg-primary/90";
  }
}

const IconLabelSubtextButton: React.FC<IconLabelSubtextButtonProps> = ({
  icon,
  label,
  subtext,
  badge,
  tooltip,
  variant = "default",
  size = "md",
  loading = false,
  success = false,
  className,
  disabled,
  ...props
}) => {
  const s = sizeClasses(size);
  const v = variantClasses(variant);

  const inner = (
    <button
      type="button"
      className={cn(
        "relative inline-flex items-center gap-3 rounded-2xl transition-colors duration-150 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-ring",
        s.padding,
        v,
        className,
        disabled && "opacity-60 cursor-not-allowed",
      )}
      disabled={disabled || loading}
      {...props}
    >
      {/* Icon */}
      <span
        className={cn("flex items-center justify-center rounded-md", s.icon)}
        aria-hidden
      >
        {loading ? (
          <Loader2 className={cn(s.icon, "animate-spin")} />
        ) : success ? (
          <Check className={cn(s.icon)} />
        ) : (
          (icon ?? <DownloadCloud className={cn(s.icon)} />)
        )}
      </span>

      {/* Text column */}
      <span className="flex flex-col items-start leading-none">
        <span className={cn(s.label)}>{label}</span>
        {subtext ? (
          <span className={cn("text-muted-foreground", s.subtext)}>
            {subtext}
          </span>
        ) : null}
      </span>

      {/* Optional small badge */}
      {badge !== undefined ? (
        <span className="absolute -top-2 -right-2">
          <Badge className="p-1 min-w-[1.25rem] h-5 text-[0.65rem]">
            {badge}
          </Badge>
        </span>
      ) : null}
    </button>
  );

  if (tooltip) {
    return (
      <TooltipProvider>
        <Tooltip>
          <TooltipTrigger asChild>{inner}</TooltipTrigger>
          <TooltipContent side="top">{tooltip}</TooltipContent>
        </Tooltip>
      </TooltipProvider>
    );
  }

  return inner;
};

export default IconLabelSubtextButton;

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

interface BadgeProps
  extends React.HTMLAttributes<HTMLSpanElement>,
    VariantProps<typeof badgeVariants> {
  asChild?: boolean;
}

const Badge = React.forwardRef<HTMLSpanElement, BadgeProps>(
  ({ className, variant, asChild = false, children, ...props }, ref) => {
    if (asChild && React.isValidElement(children)) {
      return React.cloneElement(children as React.ReactElement<any>, {
        className: cn(
          badgeVariants({ variant }),
          className,
          (children.props as any).className,
        ),
        ref,
        ...props,
      });
    }

    return (
      <span
        data-slot="badge"
        className={cn(badgeVariants({ variant }), className)}
        ref={ref}
        {...props}
      >
        {children}
      </span>
    );
  },
);

Badge.displayName = "Badge";

export { Badge, badgeVariants, type BadgeProps };

"use client";

import * as React from "react";
import { Tooltip as TooltipPrimitive } from "@base-ui/react/tooltip";

import { cn } from "@/lib/utils";

function TooltipProvider({
  children,
  ...props
}: React.ComponentProps<typeof TooltipPrimitive.Provider>) {
  return (
    <TooltipPrimitive.Provider {...props}>{children}</TooltipPrimitive.Provider>
  );
}

function Tooltip({
  ...props
}: React.ComponentProps<typeof TooltipPrimitive.Root>) {
  return <TooltipPrimitive.Root {...props} />;
}

function TooltipTrigger({
  ...props
}: React.ComponentProps<typeof TooltipPrimitive.Trigger>) {
  return <TooltipPrimitive.Trigger {...props} />;
}

const TooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof TooltipPrimitive.Popup> & {
    sideOffset?: number;
    side?: "top" | "bottom" | "left" | "right";
  }
>(({ className, sideOffset = 4, side = "top", children, ...props }, ref) => (
  <TooltipPrimitive.Portal>
    <TooltipPrimitive.Positioner sideOffset={sideOffset} side={side}>
      <TooltipPrimitive.Popup
        ref={ref}
        className={cn(
          "z-50 overflow-hidden rounded-md bg-primary px-3 py-1.5 text-xs text-primary-foreground animate-in fade-in-0 zoom-in-95 data-[closed]:animate-out data-[closed]:fade-out-0 data-[closed]:zoom-out-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2",
          className,
        )}
        {...props}
      >
        {children}
      </TooltipPrimitive.Popup>
    </TooltipPrimitive.Positioner>
  </TooltipPrimitive.Portal>
));
TooltipContent.displayName = "TooltipContent";

export { Tooltip, TooltipTrigger, TooltipContent, TooltipProvider };
