"use client";

import { CheckIcon } from "lucide-react";
import { useState } from "react";
import { motion } from "motion/react";

export default function Pricing_Plans() {
  const [billing, setBilling] = useState<"monthly" | "yearly">("monthly");

  const plans = [
    {
      label: "SPRINT",
      labelColor: "bg-yellow-100 text-yellow-700",
      title: "One-Time Setup",
      price: "$1,999",
      type: "one-time",
      button: "Send Proposal",
      description:
        "Ideal for validating your core product idea with essential design and development—fast.",
      features: [
        "2-3 core features implemented",
        "Built using Ruixen Open Source Starter Kit",
        "Basic UI/UX customization",
        "Fully responsive components",
        "Timeline: 2–4 weeks",
      ],
    },
    {
      label: "PRO",
      labelColor: "bg-indigo-100 text-indigo-700",
      title: "Monthly Subscription",
      price: billing === "monthly" ? "$499" : "$4999",
      type: billing,
      popular: true,
      button: billing === "monthly" ? "Start Monthly" : "Start Yearly",
      description:
        "Best for scaling teams needing consistent design & dev with premium Ruixen UI components.",
      features: [
        "Full access to Ruixen UI library",
        "Unlimited website components",
        "Priority support & updates",
        "Integration help (Next.js, Remix, etc.)",
        "SEO-optimized templates",
        "Ongoing performance improvements",
      ],
    },
    {
      label: "ENTERPRISE",
      labelColor: "bg-emerald-100 text-emerald-700",
      title: "Custom Solutions",
      price: "Custom",
      type: "",
      button: "Contact Us",
      description:
        "Tailored for large teams needing full-stack design, dev, & deployment support using Ruixen stack.",
      features: [
        "Dedicated solution architect",
        "Design + Dev + Hosting consultation",
        "Custom workflows & dashboards",
        "Migration from legacy UIs",
        "AI-powered UI integrations",
        "Training + Onboarding",
      ],
    },
  ];

  const [period, setPeriod] = useState(0);
  const handleChangePeriod = (index: number) => {
    setPeriod(index);
    if (index === 0) {
      setBilling("monthly");
    } else {
      setBilling("yearly");
    }
  };

  return (
    <section className="py-20 bg-background text-foreground px-4">
      <div className="max-w-5xl mx-auto text-center mb-10">
        <h2 className="text-3xl sm:text-4xl font-bold text-black dark:text-white">
          Pricing Plans for Every Stage
        </h2>
        <p className="mt-4 text-muted-foreground max-w-2xl mx-auto text-base">
          Whether you&#39;re launching or scaling, Ruixen UI supports your
          journey with expertly crafted UI &amp; services.
        </p>

        <div className="rounded-full relative w-52 mx-auto my-5 border p-1.5 flex items-center">
          <button
            className="font-semibold rounded-full w-full p-1.5 text-slate-800 dark:text-gray-100 z-20"
            onClick={() => handleChangePeriod(0)}
          >
            Monthly
          </button>
          <button
            className="font-semibold rounded-full w-full p-1.5 text-slate-800 dark:text-gray-100 z-20"
            onClick={() => handleChangePeriod(1)}
          >
            Yearly
          </button>
          <div
            className="p-1.5 flex items-center justify-center absolute inset-0 w-1/2 z-10"
            style={{
              transform: `translateX(${period * 100}%)`,
              transition: "transform 0.3s",
            }}
          >
            <div className="bg-white dark:bg-zinc-900 border border-gray-500 shadow-sm rounded-full w-full h-full"></div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-5xl mx-auto">
        {plans.map((plan, i) => (
          <motion.div
            key={i}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4, delay: i * 0.1 }}
            className={`rounded-xl border border-muted bg-white dark:bg-zinc-900 text-black dark:text-white p-4 sm:p-6 flex flex-col ${
              plan.popular ? "ring-2 ring-primary shadow-md" : ""
            }`}
          >
            <div className="mb-4">
              <span
                className={`text-xs uppercase font-semibold px-2 py-1 rounded ${plan.labelColor}`}
              >
                {plan.label}
              </span>
            </div>

            <div className="text-3xl font-bold">
              {plan.price}
              {plan.type && (
                <span className="text-sm font-medium text-muted-foreground ml-1">
                  /{plan.type}
                </span>
              )}
            </div>
            <p className="mt-3 text-sm text-muted-foreground leading-relaxed">
              {plan.description}
            </p>

            <button className="mt-6 bg-primary text-primary-foreground py-2 rounded-md font-medium text-sm hover:bg-primary/90 transition border border-gray-600 dark:border-gray-700">
              {plan.button}
            </button>

            <div className="mt-6">
              <p className="text-sm font-semibold mb-4">What&#39;s included:</p>
              <ul className="space-y-3 text-sm">
                {plan.features.map((feature, idx) => (
                  <li key={idx} className="flex items-start gap-2">
                    <CheckIcon className="w-4 h-4 mt-1 text-green-500 dark:text-green-400" />
                    <span>{feature}</span>
                  </li>
                ))}
              </ul>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background text-foreground shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
        rainbow:
          "group relative animate-rainbow cursor-pointer border-0 bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] text-primary-foreground [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
        "rainbow-outline":
          "group relative animate-rainbow cursor-pointer border-0 border-input bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] px-4 text-foreground shadow-sm [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-xl px-3 text-xs",
        md: "h-10 rounded-xl px-4 text-sm",
        lg: "h-11 rounded-xl px-8",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, children, ...props }, ref) => {
    if (asChild && React.isValidElement(children)) {
      return React.cloneElement(children as React.ReactElement<any>, {
        className: cn(
          buttonVariants({ variant, size, className }),
          (children.props as any).className,
        ),
        ref,
        ...props,
      });
    }

    return (
      <button
        data-slot="button"
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      >
        {children}
      </button>
    );
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };
