import { TabbedHeroSection } from "@/registry/ruixenui/tabbed-hero-section";
import { Globe, BarChart3, Sparkles } from "lucide-react";

/* ── demo ─────────────────────────────────────────────────────── */

export default function TabbedHeroSectionDemo() {
  return (
    <TabbedHeroSection
      title="Payments infrastructure built for SaaS that Scales"
      description="Streamline your team's meetings with our AI-driven assistant, open source and works."
      primaryAction={{ href: "#", label: "Get Started" }}
      secondaryAction={{ href: "#", label: "Watch Demo" }}
      tabs={[
        { icon: <Globe />, label: "Task Management" },
        { icon: <BarChart3 />, label: "Analytics" },
        { icon: <Sparkles />, label: "AI Copilot" },
      ]}
      activeTab={2}
      trustedBy={{
        description: (
          <p>
            Trusted by leading teams from{" "}
            <span className="text-foreground font-medium">
              Generative AI Companies,
            </span>{" "}
            <span className="text-foreground font-medium">
              Hosting Providers,
            </span>{" "}
            <span className="text-foreground font-medium">
              Payments Providers,
            </span>{" "}
            <span className="text-foreground font-medium">
              Streaming Providers
            </span>
          </p>
        ),
        logos: [
          <span
            key="openai"
            className="text-[15px] font-semibold tracking-tight whitespace-nowrap"
          >
            OpenAI
          </span>,
          <span
            key="bolt"
            className="text-[15px] font-bold italic tracking-tight whitespace-nowrap"
          >
            bolt
          </span>,
          <span
            key="cisco"
            className="text-[15px] font-semibold tracking-tight whitespace-nowrap uppercase"
          >
            Cisco
          </span>,
          <span
            key="hulu"
            className="text-[15px] font-bold tracking-tight whitespace-nowrap lowercase"
          >
            hulu
          </span>,
          <span
            key="spotify"
            className="text-[15px] font-semibold tracking-tight whitespace-nowrap"
          >
            Spotify
          </span>,
        ],
      }}
    />
  );
}
