"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { cn } from "@/lib/utils";

interface AvatarData {
  src?: string;
  alt?: string;
  fallback: string;
}

interface AvatarGroupProps {
  avatars: AvatarData[];
  max?: number;
  size?: "sm" | "md" | "lg";
  className?: string;
}

const sizeClasses = {
  sm: "size-8",
  md: "size-10",
  lg: "size-12",
};

const spacingClasses = {
  sm: "-space-x-2",
  md: "-space-x-3",
  lg: "-space-x-4",
};

export default function AvatarGroup({
  avatars,
  max = 4,
  size = "md",
  className,
}: AvatarGroupProps) {
  const displayAvatars = avatars.slice(0, max);

  return (
    <div className={cn("flex", spacingClasses[size], className)}>
      {displayAvatars.map((avatar, index) => (
        <Avatar
          key={index}
          className={cn(
            sizeClasses[size],
            "ring-2 ring-background transition-transform hover:z-10 hover:scale-110",
          )}
        >
          <AvatarImage src={avatar.src} alt={avatar.alt || avatar.fallback} />
          <AvatarFallback className="text-xs font-medium">
            {avatar.fallback}
          </AvatarFallback>
        </Avatar>
      ))}
    </div>
  );
}

"use client";

import * as React from "react";

import { cn } from "@/lib/utils";

function Avatar({ className, ...props }: React.ComponentProps<"span">) {
  return (
    <span
      data-slot="avatar"
      className={cn(
        "relative flex size-8 shrink-0 overflow-hidden rounded-full",
        className,
      )}
      {...props}
    />
  );
}

function AvatarImage({
  className,
  src,
  alt = "",
  onLoadingStatusChange,
  ...props
}: React.ComponentProps<"img"> & {
  onLoadingStatusChange?: (status: "loading" | "loaded" | "error") => void;
}) {
  const [status, setStatus] = React.useState<"loading" | "loaded" | "error">(
    "loading",
  );

  React.useEffect(() => {
    if (!src) {
      setStatus("error");
      return;
    }
    setStatus("loading");
    const img = new Image();
    img.src = src;
    img.onload = () => {
      setStatus("loaded");
      onLoadingStatusChange?.("loaded");
    };
    img.onerror = () => {
      setStatus("error");
      onLoadingStatusChange?.("error");
    };
  }, [src, onLoadingStatusChange]);

  if (status !== "loaded") return null;

  return (
    <img
      data-slot="avatar-image"
      className={cn("aspect-square size-full", className)}
      src={src}
      alt={alt}
      {...props}
    />
  );
}

function AvatarFallback({
  className,
  delayMs,
  children,
  ...props
}: React.ComponentProps<"span"> & { delayMs?: number }) {
  const [show, setShow] = React.useState(!delayMs);

  React.useEffect(() => {
    if (delayMs) {
      const timer = setTimeout(() => setShow(true), delayMs);
      return () => clearTimeout(timer);
    }
  }, [delayMs]);

  if (!show) return null;

  return (
    <span
      data-slot="avatar-fallback"
      className={cn(
        "bg-muted flex size-full items-center justify-center rounded-full",
        className,
      )}
      {...props}
    >
      {children}
    </span>
  );
}

export { Avatar, AvatarImage, AvatarFallback };
