"use client";

import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { Check, Clock, AlertTriangle, X, Loader2 } from "lucide-react";

type Status = "success" | "pending" | "warning" | "error" | "loading";

interface BadgeStatusProps {
  status?: Status;
  children?: React.ReactNode;
  showIcon?: boolean;
  showDot?: boolean;
  className?: string;
}

const statusConfig = {
  success: {
    icon: Check,
    dotColor: "bg-emerald-500",
    badgeClass:
      "bg-emerald-500/10 text-emerald-600 border-emerald-500/20 hover:bg-emerald-500/20",
    label: "Success",
  },
  pending: {
    icon: Clock,
    dotColor: "bg-blue-500",
    badgeClass:
      "bg-blue-500/10 text-blue-600 border-blue-500/20 hover:bg-blue-500/20",
    label: "Pending",
  },
  warning: {
    icon: AlertTriangle,
    dotColor: "bg-amber-500",
    badgeClass:
      "bg-amber-500/10 text-amber-600 border-amber-500/20 hover:bg-amber-500/20",
    label: "Warning",
  },
  error: {
    icon: X,
    dotColor: "bg-red-500",
    badgeClass:
      "bg-red-500/10 text-red-600 border-red-500/20 hover:bg-red-500/20",
    label: "Error",
  },
  loading: {
    icon: Loader2,
    dotColor: "bg-gray-500",
    badgeClass:
      "bg-gray-500/10 text-gray-600 border-gray-500/20 hover:bg-gray-500/20",
    label: "Loading",
  },
};

export default function BadgeStatus({
  status = "success",
  children,
  showIcon = false,
  showDot = true,
  className,
}: BadgeStatusProps) {
  const config = statusConfig[status];
  const Icon = config.icon;
  const isLoading = status === "loading";

  return (
    <Badge
      variant="outline"
      className={cn("gap-1.5 font-medium", config.badgeClass, className)}
    >
      {showDot && !showIcon && (
        <span
          className={cn(
            "size-2 rounded-full",
            config.dotColor,
            isLoading && "animate-pulse",
          )}
        />
      )}
      {showIcon && (
        <Icon className={cn("size-3", isLoading && "animate-spin")} />
      )}
      {children || config.label}
    </Badge>
  );
}

export { BadgeStatus, type BadgeStatusProps };

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

interface BadgeProps
  extends React.HTMLAttributes<HTMLSpanElement>,
    VariantProps<typeof badgeVariants> {
  asChild?: boolean;
}

const Badge = React.forwardRef<HTMLSpanElement, BadgeProps>(
  ({ className, variant, asChild = false, children, ...props }, ref) => {
    if (asChild && React.isValidElement(children)) {
      return React.cloneElement(children as React.ReactElement<any>, {
        className: cn(
          badgeVariants({ variant }),
          className,
          (children.props as any).className,
        ),
        ref,
        ...props,
      });
    }

    return (
      <span
        data-slot="badge"
        className={cn(badgeVariants({ variant }), className)}
        ref={ref}
        {...props}
      >
        {children}
      </span>
    );
  },
);

Badge.displayName = "Badge";

export { Badge, badgeVariants, type BadgeProps };
