"use client";

import * as React from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { DayPicker } from "react-day-picker";

import { cn } from "@/lib/utils";
import { buttonVariants } from "@/components/ui/button";

export type CalendarProps = React.ComponentProps<typeof DayPicker>;

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  components: userComponents,
  ...props
}: CalendarProps) {
  const defaultClassNames = {
    months: "relative flex flex-col sm:flex-row gap-6",
    month: "w-full",
    month_caption:
      "relative mb-2 flex h-9 items-center justify-center text-base font-semibold text-foreground",
    caption_label: "text-center text-sm font-medium",
    nav: "absolute top-1 flex w-full justify-between px-2 z-10",
    button_previous: cn(
      buttonVariants({ variant: "ghost", size: "icon" }),
      "size-8 rounded-full text-muted-foreground hover:text-foreground",
    ),
    button_next: cn(
      buttonVariants({ variant: "ghost", size: "icon" }),
      "size-8 rounded-full text-muted-foreground hover:text-foreground",
    ),

    // ✅ keep 7 columns aligned with day cell width
    weekdays:
      "grid grid-cols-7 text-center text-xs font-medium uppercase text-muted-foreground/80",
    weekday: "py-1 w-10",

    // ✅ this is where “too close” usually comes from (no gap + small cells)
    // Fix: make each day cell slightly bigger (w/h) and center the button inside it.
    week: "grid grid-cols-7",
    day: "group relative flex size-10 items-center justify-center p-0 text-center",

    // ✅ button remains slightly smaller than the cell, creating natural spacing
    day_button:
      "relative flex size-9 items-center justify-center rounded-full text-sm transition-all " +
      "hover:bg-accent hover:text-accent-foreground " +
      "focus:outline-none focus-visible:ring-2 focus-visible:ring-ring/70 " +
      "group-data-[selected]:bg-black group-data-[selected]:text-white " +
      "dark:group-data-[selected]:bg-white dark:group-data-[selected]:text-black " +
      "group-data-[selected]:shadow-md " +
      "group-data-[disabled]:opacity-40 group-data-[disabled]:cursor-not-allowed " +
      "group-data-[disabled]:hover:bg-transparent group-data-[disabled]:hover:text-muted-foreground/40",

    // Range styles (these apply to the day cell modifier in DayPicker)
    range_start:
      "rounded-l-full bg-black text-white dark:bg-white dark:text-black shadow-md",
    range_end:
      "rounded-r-full bg-black text-white dark:bg-white dark:text-black shadow-md",
    range_middle:
      "bg-black/10 text-foreground dark:bg-white/20 rounded-none transition-colors",

    // Today dot (works reliably because day is `relative`)
    today:
      "after:absolute after:bottom-1 after:left-1/2 after:-translate-x-1/2 after:w-1.5 after:h-1.5 after:rounded-full after:bg-primary",

    outside:
      "text-muted-foreground/50 hover:text-accent-foreground hover:bg-accent/30",
    hidden: "invisible",

    week_number:
      "flex size-10 items-center justify-center p-0 text-xs font-medium text-muted-foreground/80",
  } as const;

  const mergedClassNames = (
    Object.keys(defaultClassNames) as Array<keyof typeof defaultClassNames>
  ).reduce(
    (acc, key) => {
      const userClassName = classNames?.[key as keyof typeof classNames] as
        | string
        | undefined;

      return {
        ...acc,
        [key]: userClassName
          ? cn(defaultClassNames[key], userClassName)
          : defaultClassNames[key],
      };
    },
    {} as typeof defaultClassNames,
  );

  const defaultComponents = {
    Chevron: ({ orientation, ...iconProps }: any) => {
      const Icon = orientation === "left" ? ChevronLeft : ChevronRight;
      return (
        <Icon size={18} strokeWidth={2} {...iconProps} aria-hidden="true" />
      );
    },
  };

  const mergedComponents = {
    ...defaultComponents,
    ...userComponents,
  };

  return (
    <DayPicker
      showOutsideDays={showOutsideDays}
      className={cn("w-fit p-3 bg-card rounded-xl border shadow-sm", className)}
      classNames={mergedClassNames}
      components={mergedComponents}
      {...props}
    />
  );
}

Calendar.displayName = "Calendar";

export { Calendar };

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background text-foreground shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
        rainbow:
          "group relative animate-rainbow cursor-pointer border-0 bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] text-primary-foreground [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
        "rainbow-outline":
          "group relative animate-rainbow cursor-pointer border-0 border-input bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] px-4 text-foreground shadow-sm [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-xl px-3 text-xs",
        md: "h-10 rounded-xl px-4 text-sm",
        lg: "h-11 rounded-xl px-8",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, children, ...props }, ref) => {
    if (asChild && React.isValidElement(children)) {
      return React.cloneElement(children as React.ReactElement<any>, {
        className: cn(
          buttonVariants({ variant, size, className }),
          (children.props as any).className,
        ),
        ref,
        ...props,
      });
    }

    return (
      <button
        data-slot="button"
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      >
        {children}
      </button>
    );
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };
