"use client";

import * as React from "react";
import { motion } from "motion/react";
import { cn } from "@/lib/utils";

const DOWN_INDICES = new Set([9, 10, 21, 22, 23, 31]);
const BAR_COUNT = 38;

/* ── spring presets ─────────────────────────────────────────────── */
const SPRING_CARD = { type: "spring" as const, stiffness: 280, damping: 32 };
const SPRING_SOFT = { type: "spring" as const, stiffness: 220, damping: 28 };
const SPRING_SNAPPY = { type: "spring" as const, stiffness: 400, damping: 30 };

/* ── marks ── geometric, monochrome, one shape per concept ─────── */
function CanvasMark() {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 16 16"
      fill="none"
      className="mb-4 size-4"
      aria-hidden="true"
    >
      <circle cx="6" cy="8" r="5.5" className="fill-foreground/[0.10]" />
      <circle cx="10" cy="8" r="5.5" className="fill-foreground/[0.06]" />
    </svg>
  );
}

function PulseMark() {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 16 16"
      fill="none"
      className="mb-4 size-4"
      aria-hidden="true"
    >
      <path
        d="M0.5 8H4L6 3L8 13L10 5L12 8H15.5"
        className="stroke-foreground/[0.14]"
        strokeWidth="1.5"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}

function SparkleMark() {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 16 16"
      fill="none"
      className="mb-4 size-4"
      aria-hidden="true"
    >
      <path
        d="M8 1L9.2 6.8L15 8L9.2 9.2L8 15L6.8 9.2L1 8L6.8 6.8Z"
        className="fill-foreground/[0.10]"
      />
    </svg>
  );
}

/* ── card shell — invisible container, content speaks ────────── */
function FeatureCard({
  children,
  className,
  index,
}: {
  children: React.ReactNode;
  className?: string;
  index: number;
}) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ ...SPRING_CARD, delay: index * 0.12 }}
      className={cn(
        "border border-foreground/[0.06] bg-background p-5 sm:p-6",
        "grid grid-rows-[auto_1fr] gap-8 overflow-hidden sm:gap-10",
        className,
      )}
    >
      {children}
    </motion.div>
  );
}

/* ── main ───────────────────────────────────────────────────────── */
export default function IntegrationAndStatsSection() {
  return (
    <section className="mx-auto w-full max-w-5xl px-4 py-8 sm:px-6 sm:py-10 lg:py-12">
      <motion.p
        initial={{ opacity: 0, y: 8 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ ...SPRING_SOFT }}
        className="mb-8 max-w-2xl text-2xl leading-snug tracking-tight sm:text-3xl sm:leading-snug"
      >
        <span className="font-semibold text-foreground">
          Build at full speed.
        </span>{" "}
        <span className="text-muted-foreground">
          Collaborate in real-time, monitor every deploy, and let AI handle
          the rest — all in one surface.
        </span>
      </motion.p>

      <div className="grid grid-cols-2">
        {/* ── Live Canvas ──────────────────────────────────────── */}
        <FeatureCard index={0} className="col-span-2">
          <div>
            <CanvasMark />
            <h3 className="text-base font-semibold tracking-tight text-foreground sm:text-lg">
              Live Canvas
            </h3>
            <p className="mt-2.5 text-[13px] leading-relaxed text-muted-foreground sm:mt-3 sm:text-sm">
              Build together with{" "}
              <span className="font-medium text-foreground">
                multiplayer cursors
              </span>{" "}
              that never miss a beat.
            </p>
          </div>

          <div
            className="-m-5 flex flex-col justify-end p-5 sm:-m-6 sm:p-6"
            aria-hidden="true"
          >
            <motion.div
              initial={{ opacity: 0, y: 8 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ ...SPRING_SOFT, delay: 0.25 }}
              className="mask-radial-[100%_100%] mask-radial-from-75% mask-radial-at-top w-full"
            >
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ ...SPRING_SOFT, delay: 0.35 }}
                className="mb-4 text-xl font-medium text-foreground"
              >
                Design System
              </motion.div>

              <div className="space-y-3 text-base/6 text-muted-foreground">
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ ...SPRING_SOFT, delay: 0.45 }}
                >
                  Docu
                  <motion.span
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ ...SPRING_SOFT, delay: 0.55 }}
                    className="before:mask-l-from-65% relative select-none bg-linear-to-r to-indigo-500/35 text-indigo-600 before:absolute before:inset-0 before:border before:border-indigo-500/45 dark:text-indigo-300"
                  >
                    ment component
                  </motion.span>{" "}
                  <span className="relative inline-block">
                    <motion.div
                      initial={{ opacity: 0, y: 4 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ ...SPRING_SNAPPY, delay: 0.65 }}
                      className="before:h-9.5 bg-primary before:bg-primary absolute top-0.5 flex -translate-x-0.5 -translate-y-full select-none items-center gap-1.5 rounded-r-md rounded-tl-md px-1.5 py-1 text-white shadow-md shadow-black/[0.065] before:absolute before:left-0 before:top-2 before:w-0.5 before:rounded"
                    >
                      <div className="before:border-foreground/20 relative size-4 overflow-hidden rounded-full bg-primary/80 before:absolute before:inset-0 before:rounded-full before:border">
                        <span className="flex size-full items-center justify-center text-[7px] font-bold text-white">
                          E
                        </span>
                      </div>
                      <span className="text-xs font-medium">Elena</span>
                    </motion.div>
                    variants
                  </span>{" "}
                  and tokens.
                </motion.div>

                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ ...SPRING_SOFT, delay: 0.55 }}
                >
                  Standardize patterns, review accessibility, and ship{" "}
                  <span className="relative inline-block">
                    <motion.div
                      initial={{ opacity: 0, y: 4 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ ...SPRING_SNAPPY, delay: 0.8 }}
                      className="before:h-9.5 absolute top-0.5 flex -translate-x-0.5 -translate-y-full select-none items-center gap-1.5 rounded-r-md rounded-tl-md bg-emerald-600 px-1.5 py-1 text-white shadow-md before:absolute before:left-0 before:top-2 before:w-0.5 before:rounded before:bg-emerald-600"
                    >
                      <div className="before:border-foreground/20 relative size-4 overflow-hidden rounded-full bg-emerald-400 before:absolute before:inset-0 before:rounded-full before:border">
                        <span className="flex size-full items-center justify-center text-[7px] font-bold text-white">
                          J
                        </span>
                      </div>
                      <span className="truncate text-xs font-medium">
                        James K.
                      </span>
                    </motion.div>
                    consistent
                  </span>{" "}
                  interfaces across every surface.
                </motion.div>
              </div>
            </motion.div>
          </div>
        </FeatureCard>

        {/* ── System Pulse ─────────────────────────────────────── */}
        <FeatureCard index={1}>
          <div>
            <PulseMark />
            <h3 className="text-base font-semibold tracking-tight text-foreground sm:text-lg">
              System Pulse
            </h3>
            <p className="mt-2.5 text-[13px] leading-relaxed text-muted-foreground sm:mt-3 sm:text-sm">
              One glance at your entire stack with{" "}
              <span className="font-medium text-foreground">
                real-time telemetry
              </span>
              .
            </p>
          </div>

          <div className="-m-5 flex flex-col justify-end p-5 sm:-m-6 sm:p-6">
            <div className="w-full">
              <motion.div
                initial={{ opacity: 0, y: 8 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ ...SPRING_SOFT, delay: 0.3 }}
                aria-hidden="true"
                className="space-y-3 p-3 sm:p-4"
              >
                {/* hero metric */}
                <div>
                  <span className="text-[11px] text-foreground/40">
                    Throughput
                  </span>
                  <div className="mt-0.5 flex items-baseline gap-1.5">
                    <motion.span
                      initial={{ opacity: 0, scale: 0.95 }}
                      animate={{ opacity: 1, scale: 1 }}
                      transition={{ ...SPRING_SOFT, delay: 0.4 }}
                      className="text-xl font-semibold tracking-tight tabular-nums text-foreground sm:text-2xl"
                    >
                      14.2k
                    </motion.span>
                    <motion.span
                      initial={{ opacity: 0, x: -4 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ ...SPRING_SOFT, delay: 0.55 }}
                      className="text-[11px] font-medium text-blue-500/70"
                    >
                      +8.7%
                    </motion.span>
                  </div>
                </div>

                {/* bars cascade from bottom, staggered left → right */}
                <div className="flex gap-px">
                  {Array.from({ length: BAR_COUNT }, (_, i) => (
                    <motion.div
                      key={i}
                      initial={{ scaleY: 0 }}
                      animate={{ scaleY: 1 }}
                      transition={{
                        ...SPRING_SNAPPY,
                        delay: 0.5 + i * 0.018,
                      }}
                      style={{ transformOrigin: "bottom" }}
                      className={cn(
                        "h-6 flex-1",
                        DOWN_INDICES.has(i)
                          ? "bg-foreground/25"
                          : "bg-blue-500",
                      )}
                    />
                  ))}
                </div>

                {/* secondary metrics */}
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ ...SPRING_SOFT, delay: 0.95 }}
                  className="flex gap-4 border-t border-foreground/[0.06] pt-2.5"
                >
                  <div>
                    <span className="text-[10px] text-foreground/30">
                      Latency
                    </span>
                    <div className="text-xs font-medium tabular-nums text-foreground/55">
                      23ms
                    </div>
                  </div>
                  <div>
                    <span className="text-[10px] text-foreground/30">P99</span>
                    <div className="text-xs font-medium tabular-nums text-foreground/55">
                      89ms
                    </div>
                  </div>
                  <div>
                    <span className="text-[10px] text-foreground/30">
                      Errors
                    </span>
                    <div className="text-xs font-medium tabular-nums text-foreground/55">
                      0.02%
                    </div>
                  </div>
                </motion.div>
              </motion.div>
            </div>
          </div>
        </FeatureCard>

        {/* ── AI Copilot ───────────────────────────────────────── */}
        <FeatureCard index={2} className="">
          <div>
            <SparkleMark />
            <h3 className="text-base font-semibold tracking-tight text-foreground sm:text-lg">
              AI Copilot
            </h3>
            <p className="mt-2.5 text-[13px] leading-relaxed text-muted-foreground sm:mt-3 sm:text-sm">
              Generate, refine, and ship with a{" "}
              <span className="font-medium text-foreground">single prompt</span>
              .
            </p>
          </div>

          <div
            className="-m-5 flex flex-col justify-end p-5 sm:-m-6 sm:p-6"
            aria-hidden="true"
          >
            <motion.div
              initial={{ opacity: 0, y: 8 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ ...SPRING_SOFT, delay: 0.3 }}
              className="relative min-w-0"
            >
              {/* floating instruction card */}
              <div className="absolute inset-x-6 top-6 z-[1]">
                {/* gradient glow */}
                <div className="absolute inset-0 scale-100 opacity-75 blur-lg transition-all duration-300 dark:opacity-50">
                  <motion.div
                    animate={{
                      filter: ["hue-rotate(0deg)", "hue-rotate(360deg)"],
                    }}
                    transition={{
                      duration: 8,
                      repeat: Infinity,
                      ease: "linear",
                    }}
                    className="absolute inset-x-6 bottom-0 top-12 -translate-y-3 bg-linear-to-r from-pink-400 to-purple-400"
                  />
                </div>

                {/* card */}
                <motion.div
                  initial={{ opacity: 0, y: 6, scale: 0.98 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  transition={{ ...SPRING_SNAPPY, delay: 0.5 }}
                  className="relative rounded-xl bg-card/95 shadow-lg shadow-black/[0.065] ring-1 ring-border backdrop-blur"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    className="absolute right-2 top-2 size-3 text-muted-foreground/50"
                  >
                    <path d="M18 6 6 18" />
                    <path d="m6 6 12 12" />
                  </svg>
                  <span className="block p-3 text-xs text-muted-foreground">
                    Enter instruction
                  </span>
                  <div className="flex items-center justify-between border-t p-2">
                    <span className="flex h-6 items-center gap-1.5 rounded-md p-2 text-muted-foreground">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        className="size-3.5 opacity-75"
                      >
                        <path d="M12 20.94c1.5 0 2.75 1.06 4 1.06 3 0 6-8 6-12.22A4.91 4.91 0 0 0 17 5c-2.22 0-4 1.44-5 2-1-.56-2.78-2-5-2a4.9 4.9 0 0 0-5 4.78C2 14 5 22 8 22c1.25 0 2.5-1.06 4-1.06Z" />
                        <path d="M10 2c1 .5 2 2 2 5" />
                      </svg>
                      <span className="text-xs">Professional</span>
                    </span>
                    <div className="relative flex size-6 rounded-md bg-primary text-white shadow before:absolute before:inset-0 before:rounded-md before:border before:border-foreground/20">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        className="m-auto size-3.5 drop-shadow"
                      >
                        <polyline points="9 10 4 15 9 20" />
                        <path d="M20 4v7a4 4 0 0 1-4 4H4" />
                      </svg>
                    </div>
                  </div>
                </motion.div>
              </div>

              {/* background text */}
              <div className="mask-b-from-25% mask-b-to-85% space-y-3 text-sm leading-relaxed">
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ ...SPRING_SOFT, delay: 0.4 }}
                  className="text-muted-foreground"
                >
                  Web applications with{" "}
                  <span className="rounded bg-linear-to-r from-primary to-emerald-500 bg-clip-text px-1 text-transparent">
                    React and TypeScript
                  </span>{" "}
                  using best practices.
                </motion.div>
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ ...SPRING_SOFT, delay: 0.5 }}
                  className="text-muted-foreground"
                >
                  This guide covers component architecture, state management,
                  and performance optimization techniques.
                </motion.div>
              </div>
            </motion.div>
          </div>
        </FeatureCard>
      </div>
    </section>
  );
}
