"use client";

import React, { useEffect, useRef, useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
} from "@/components/ui/popover";
import { Slider } from "@/components/ui/slider";
import {
  Play,
  Pause,
  Volume2,
  Volume1,
  VolumeX,
  Maximize2,
  RotateCw,
  Settings,
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";

interface VideoPlayerProProps {
  src: string;
  poster?: string;
  className?: string;
}

const formatTime = (seconds: number) => {
  if (!isFinite(seconds) || seconds < 0) return "0:00";
  const m = Math.floor(seconds / 60);
  const s = Math.floor(seconds % 60);
  return `${m}:${s.toString().padStart(2, "0")}`;
};

const VideoPlayerPro: React.FC<VideoPlayerProProps> = ({
  src,
  poster,
  className,
}) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const lastVolumeRef = useRef<number>(1);

  const [isPlaying, setIsPlaying] = useState(false);
  const [isEnded, setIsEnded] = useState(false);

  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);

  const [progress, setProgress] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);

  const [showControls, setShowControls] = useState(true);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);

  const [loadError, setLoadError] = useState<string | null>(null);

  // Reload/reset cleanly when src changes
  useEffect(() => {
    const v = videoRef.current;
    setLoadError(null);
    setIsPlaying(false);
    setIsEnded(false);
    setProgress(0);
    setCurrentTime(0);
    setDuration(0);

    if (!v) return;
    try {
      v.pause();
      v.load(); // important when using <source> or when src changes
    } catch {
      // ignore
    }
  }, [src]);

  const updateTimeState = () => {
    const v = videoRef.current;
    if (!v) return;

    const d = v.duration;
    const t = v.currentTime;

    setDuration(isFinite(d) ? d : 0);
    setCurrentTime(isFinite(t) ? t : 0);

    const prog = d > 0 ? (t / d) * 100 : 0;
    setProgress(isFinite(prog) ? prog : 0);
  };

  // Robust play with catch (prevents "Uncaught (in promise)")
  const safePlay = async () => {
    const v = videoRef.current;
    if (!v) return;

    try {
      await v.play();
      // state will be updated by onPlay handler
    } catch (err: any) {
      // This is where your error originates
      console.error("video.play() failed:", err);

      const mediaErr = v.error;
      const code = mediaErr?.code;

      // Helpful message based on MediaError codes
      // 1: aborted, 2: network, 3: decode, 4: src not supported
      const msg =
        code === 2
          ? "Network error while loading video (blocked/404/403/CSP?)."
          : code === 3
            ? "Decode error (codec not supported or corrupted file)."
            : code === 4
              ? "Source not supported (bad URL/HTML response/wrong MIME/codec)."
              : "Playback failed.";

      setLoadError(msg);
      setIsPlaying(false);
    }
  };

  const togglePlay = async () => {
    const v = videoRef.current;
    if (!v) return;

    // If ended, restart
    if (isEnded) {
      v.currentTime = 0;
      setIsEnded(false);
    }

    if (v.paused) {
      await safePlay();
    } else {
      v.pause();
    }
  };

  const handleSeek = (percent: number) => {
    const v = videoRef.current;
    if (!v) return;

    const d = v.duration || 0;
    const time = (percent / 100) * d;

    if (isFinite(time)) {
      v.currentTime = time;
      setProgress(percent);
      setCurrentTime(time);
    }
  };

  const toggleFullscreen = () => {
    const el = containerRef.current;
    if (!el) return;

    if (!document.fullscreenElement) {
      el.requestFullscreen().catch((err) => {
        console.error("Fullscreen request failed:", err);
      });
    } else {
      document.exitFullscreen().catch((err) => {
        console.error("Exit fullscreen failed:", err);
      });
    }
  };

  const setVideoVolume = (v: number) => {
    const video = videoRef.current;
    if (!video) return;

    const clamped = Math.min(1, Math.max(0, v));
    video.volume = clamped;

    setVolume(clamped);
    const mutedNow = clamped === 0;
    setIsMuted(mutedNow);
    video.muted = mutedNow;

    if (!mutedNow) lastVolumeRef.current = clamped;
  };

  const toggleMute = () => {
    const v = videoRef.current;
    if (!v) return;

    if (v.muted || volume === 0) {
      // unmute -> restore last volume
      v.muted = false;
      const restore = lastVolumeRef.current > 0 ? lastVolumeRef.current : 1;
      setVideoVolume(restore);
    } else {
      // mute
      lastVolumeRef.current = volume;
      v.muted = true;
      setIsMuted(true);
      setVolume(0);
      v.volume = 0;
    }
  };

  const VolumeIcon =
    isMuted || volume === 0 ? VolumeX : volume > 0.5 ? Volume2 : Volume1;

  return (
    <motion.div
      ref={containerRef}
      className={cn(
        "relative w-full max-w-5xl mx-auto overflow-hidden rounded-xl bg-black",
        className,
      )}
      onMouseEnter={() => setShowControls(true)}
      onMouseLeave={() => setShowControls(false)}
      onTouchStart={() => setShowControls(true)}
    >
      <video
        ref={videoRef}
        className="w-full h-auto"
        poster={poster}
        preload="metadata"
        playsInline
        // crossOrigin can help in some environments (esp. if later you draw to canvas)
        crossOrigin="anonymous"
        onClick={togglePlay}
        onTimeUpdate={updateTimeState}
        onLoadedMetadata={updateTimeState}
        onDurationChange={updateTimeState}
        onPlay={() => {
          setIsPlaying(true);
          setLoadError(null);
        }}
        onPause={() => setIsPlaying(false)}
        onEnded={() => {
          setIsEnded(true);
          setIsPlaying(false);
        }}
        onError={() => {
          const v = videoRef.current;
          const code = v?.error?.code;

          console.error("Video element error:", {
            code,
            src,
            networkState: v?.networkState,
            readyState: v?.readyState,
            currentSrc: v?.currentSrc,
          });

          const msg =
            code === 2
              ? "Network error while loading video (blocked/404/403/CSP?)."
              : code === 3
                ? "Decode error (codec not supported or corrupted file)."
                : code === 4
                  ? "Source not supported (bad URL/HTML response/wrong MIME/codec)."
                  : "Video failed to load.";

          setLoadError(msg);
          setIsPlaying(false);
        }}
      >
        {/* Using <source> helps some browsers pick types more reliably */}
        <source src={src} type="video/mp4" />
      </video>

      {/* Error overlay (so you know instantly what happened) */}
      {loadError && (
        <div className="absolute inset-0 flex items-center justify-center p-4 bg-black/70">
          <div className="max-w-md text-center text-white space-y-2">
            <div className="text-sm font-semibold">Video canâ€™t be played</div>
            <div className="text-xs opacity-90">{loadError}</div>
            <div className="text-[11px] opacity-70 break-all">{src}</div>
            <Button
              variant="secondary"
              size="sm"
              onClick={() => {
                setLoadError(null);
                videoRef.current?.load();
              }}
            >
              Retry
            </Button>
          </div>
        </div>
      )}

      {/* Controls */}
      <AnimatePresence>
        {showControls && !loadError && (
          <motion.div
            className="absolute bottom-4 left-1/2 -translate-x-1/2 w-[95%] backdrop-blur-xl bg-white/10 rounded-2xl p-3 flex flex-col gap-3"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: 20, opacity: 0 }}
            onClick={(e) => e.stopPropagation()}
          >
            {/* Progress */}
            <div
              className="relative w-full h-2 bg-white/20 rounded-full cursor-pointer"
              onClick={(e) => {
                const rect = e.currentTarget.getBoundingClientRect();
                const x = e.clientX - rect.left;
                handleSeek((x / rect.width) * 100);
              }}
            >
              <motion.div
                className="absolute top-0 left-0 h-full bg-white/70 rounded-full"
                style={{ width: `${progress}%` }}
              />
            </div>

            {/* Row */}
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                {/* Play/Pause/Restart */}
                <Button
                  variant="ghost"
                  size="icon"
                  className="text-white"
                  onClick={togglePlay}
                >
                  {isEnded ? (
                    <RotateCw className="w-5 h-5" />
                  ) : isPlaying ? (
                    <Pause className="w-5 h-5" />
                  ) : (
                    <Play className="w-5 h-5" />
                  )}
                </Button>

                {/* Volume */}
                <Popover>
                  <PopoverTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="text-white"
                      onClick={toggleMute}
                    >
                      <VolumeIcon className="w-5 h-5" />
                    </Button>
                  </PopoverTrigger>

                  <PopoverContent className="w-32 bg-transparent border-none p-2">
                    <Slider
                      value={[Math.round(volume * 100)]}
                      onValueChange={(val: number[]) => {
                        setVideoVolume((val?.[0] ?? 0) / 100);
                      }}
                      step={1}
                      min={0}
                      max={100}
                      className="w-full"
                    />
                  </PopoverContent>
                </Popover>

                {/* Time */}
                <span className="text-white text-sm tabular-nums">
                  {formatTime(currentTime)} / {formatTime(duration)}
                </span>
              </div>

              <div className="flex items-center gap-2">
                {/* Settings */}
                <Popover>
                  <PopoverTrigger asChild>
                    <Button variant="ghost" size="icon" className="text-white">
                      <Settings className="w-5 h-5" />
                    </Button>
                  </PopoverTrigger>
                  <PopoverContent className="bg-background w-40 p-2">
                    <div className="flex flex-col gap-2">
                      <span className="text-sm font-medium text-muted-foreground">
                        Speed
                      </span>
                      {[0.5, 1, 1.5, 2].map((s) => (
                        <Button
                          key={s}
                          variant={playbackSpeed === s ? "default" : "outline"}
                          size="sm"
                          className="w-full"
                          onClick={() => {
                            const v = videoRef.current;
                            if (v) v.playbackRate = s;
                            setPlaybackSpeed(s);
                          }}
                        >
                          {s}x
                        </Button>
                      ))}
                    </div>
                  </PopoverContent>
                </Popover>

                {/* Fullscreen */}
                <Button
                  variant="ghost"
                  size="icon"
                  className="text-white"
                  onClick={toggleFullscreen}
                >
                  <Maximize2 className="w-5 h-5" />
                </Button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default VideoPlayerPro;

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background text-foreground shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
        rainbow:
          "group relative animate-rainbow cursor-pointer border-0 bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] text-primary-foreground [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
        "rainbow-outline":
          "group relative animate-rainbow cursor-pointer border-0 border-input bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] bg-[length:200%] px-4 text-foreground shadow-sm [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,var(--color-1),var(--color-5),var(--color-3),var(--color-4),var(--color-2))]",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-xl px-3 text-xs",
        md: "h-10 rounded-xl px-4 text-sm",
        lg: "h-11 rounded-xl px-8",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, children, ...props }, ref) => {
    if (asChild && React.isValidElement(children)) {
      return React.cloneElement(children as React.ReactElement<any>, {
        className: cn(
          buttonVariants({ variant, size, className }),
          (children.props as any).className,
        ),
        ref,
        ...props,
      });
    }

    return (
      <button
        data-slot="button"
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      >
        {children}
      </button>
    );
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };

"use client";

import * as React from "react";
import { Popover as PopoverPrimitive } from "@base-ui/react/popover";

import { cn } from "@/lib/utils";

function Popover({
  ...props
}: React.ComponentProps<typeof PopoverPrimitive.Root>) {
  return <PopoverPrimitive.Root data-slot="popover" {...props} />;
}

function PopoverTrigger({
  ...props
}: React.ComponentProps<typeof PopoverPrimitive.Trigger>) {
  return <PopoverPrimitive.Trigger data-slot="popover-trigger" {...props} />;
}

function PopoverContent({
  className,
  align = "center",
  sideOffset = 4,
  side = "bottom",
  ...props
}: React.ComponentProps<typeof PopoverPrimitive.Popup> & {
  align?: "start" | "center" | "end";
  sideOffset?: number;
  side?: "top" | "bottom" | "left" | "right";
}) {
  return (
    <PopoverPrimitive.Portal>
      <PopoverPrimitive.Positioner
        sideOffset={sideOffset}
        side={side}
        align={align}
      >
        <PopoverPrimitive.Popup
          data-slot="popover-content"
          className={cn(
            "bg-popover text-popover-foreground data-[open]:animate-in data-[closed]:animate-out data-[closed]:fade-out-0 data-[open]:fade-in-0 data-[closed]:zoom-out-95 data-[open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2 z-50 w-72 rounded-md border p-4 shadow-md outline-hidden",
            className,
          )}
          {...props}
        />
      </PopoverPrimitive.Positioner>
    </PopoverPrimitive.Portal>
  );
}

function PopoverAnchor({ ...props }: React.ComponentProps<"div">) {
  return <div data-slot="popover-anchor" {...props} />;
}

export { Popover, PopoverTrigger, PopoverContent, PopoverAnchor };

"use client";

import * as React from "react";
import { Slider as SliderPrimitive } from "@base-ui/react/slider";

import { cn } from "@/lib/utils";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";

const Slider = React.forwardRef<
  HTMLDivElement,
  Omit<React.ComponentProps<typeof SliderPrimitive.Root>, "onValueChange"> & {
    showTooltip?: boolean;
    tooltipContent?: (value: number) => React.ReactNode;
    onValueChange?: (value: number[]) => void;
  }
>(
  (
    { className, showTooltip = false, tooltipContent, onValueChange, ...props },
    ref,
  ) => {
    const [showTooltipState, setShowTooltipState] = React.useState(false);
    const [internalValue, setInternalValue] = React.useState<number[]>(
      Array.isArray(props.defaultValue)
        ? props.defaultValue
        : Array.isArray(props.value)
          ? props.value
          : ([props.defaultValue ?? props.value ?? 0] as number[]),
    );

    React.useEffect(() => {
      if (props.value !== undefined) {
        setInternalValue(
          Array.isArray(props.value) ? props.value : [props.value],
        );
      }
    }, [props.value]);

    const handleValueChange = (newValue: number | number[]) => {
      const arr = Array.isArray(newValue) ? newValue : [newValue];
      setInternalValue(arr);
      onValueChange?.(arr);
    };

    const handlePointerDown = () => {
      if (showTooltip) {
        setShowTooltipState(true);
      }
    };

    const handlePointerUp = React.useCallback(() => {
      if (showTooltip) {
        setShowTooltipState(false);
      }
    }, [showTooltip]);

    React.useEffect(() => {
      if (showTooltip) {
        document.addEventListener("pointerup", handlePointerUp);
        return () => {
          document.removeEventListener("pointerup", handlePointerUp);
        };
      }
    }, [showTooltip, handlePointerUp]);

    const renderThumb = (value: number, index: number) => {
      const thumb = (
        <SliderPrimitive.Thumb
          key={index}
          className="block h-5 w-5 rounded-full border-2 border-primary bg-background transition-colors focus-visible:outline focus-visible:outline-[3px] focus-visible:outline-ring/40 data-[disabled]:cursor-not-allowed"
          onPointerDown={handlePointerDown}
        />
      );

      if (!showTooltip) return thumb;

      return (
        <TooltipProvider key={index}>
          <Tooltip open={showTooltipState}>
            <TooltipTrigger asChild>{thumb}</TooltipTrigger>
            <TooltipContent
              className="px-2 py-1 text-xs"
              sideOffset={8}
              side={props.orientation === "vertical" ? "right" : "top"}
            >
              <p>{tooltipContent ? tooltipContent(value) : value}</p>
            </TooltipContent>
          </Tooltip>
        </TooltipProvider>
      );
    };

    return (
      <SliderPrimitive.Root
        ref={ref}
        className={cn(
          "relative flex w-full touch-none select-none items-center data-[orientation=vertical]:h-full data-[orientation=vertical]:w-auto data-[orientation=vertical]:flex-col data-[disabled]:opacity-50",
          className,
        )}
        onValueChange={handleValueChange}
        {...props}
      >
        <SliderPrimitive.Control>
          <SliderPrimitive.Track className="relative grow overflow-hidden rounded-full bg-secondary data-[orientation=horizontal]:h-2 data-[orientation=vertical]:h-full data-[orientation=horizontal]:w-full data-[orientation=vertical]:w-2">
            <SliderPrimitive.Indicator className="absolute bg-primary data-[orientation=horizontal]:h-full data-[orientation=vertical]:w-full" />
          </SliderPrimitive.Track>
          {internalValue?.map((value, index) => renderThumb(value, index))}
        </SliderPrimitive.Control>
      </SliderPrimitive.Root>
    );
  },
);
Slider.displayName = "Slider";

export { Slider };
