"use client";

import * as React from "react";
import NavbarDashboard from "@/registry/ruixenui/navbar-dashboard";
import { LayoutDashboard } from "lucide-react";

export default function NavbarDashboardDemo() {
  const [isDark, setIsDark] = React.useState(false);

  return (
    <div className="flex min-h-[400px] w-full flex-col items-center justify-start">
      <NavbarDashboard
        logo={
          <div className="flex items-center gap-2">
            <LayoutDashboard className="size-6 text-primary" />
            <span className="text-xl font-bold">Dashboard</span>
          </div>
        }
        user={{
          name: "John Doe",
          email: "john@example.com",
          initials: "JD",
        }}
        notifications={[
          {
            id: "1",
            title: "New message",
            description: "You have a new message from Sarah",
            time: "5 min ago",
            read: false,
          },
          {
            id: "2",
            title: "Task completed",
            description: "Project review has been completed",
            time: "1 hour ago",
            read: false,
          },
          {
            id: "3",
            title: "Meeting reminder",
            description: "Team standup in 15 minutes",
            time: "2 hours ago",
            read: true,
          },
        ]}
        showThemeToggle
        isDarkMode={isDark}
        onThemeToggle={() => setIsDark(!isDark)}
        onLogout={() => console.log("Logout")}
      />
      <div className="flex flex-1 items-center justify-center p-8">
        <p className="text-muted-foreground">
          Dashboard content goes here
        </p>
      </div>
    </div>
  );
}
