"use client";

import * as React from "react";
import Link from "next/link";
import {
  Menu,
  X,
  ShoppingCart,
  Heart,
  Search,
  User,
  ChevronDown,
} from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export interface NavbarEcommerceCategory {
  label: string;
  href: string;
  subcategories?: { label: string; href: string }[];
}

export interface NavbarEcommerceProps {
  logo?: React.ReactNode;
  categories?: NavbarEcommerceCategory[];
  cartCount?: number;
  wishlistCount?: number;
  onSearch?: (query: string) => void;
  className?: string;
}

export default function NavbarEcommerce({
  logo = <span className="text-xl font-bold">Store</span>,
  categories = [
    {
      label: "Products",
      href: "/products",
      subcategories: [
        { label: "New Arrivals", href: "/products/new" },
        { label: "Best Sellers", href: "/products/best" },
        { label: "Sale", href: "/products/sale" },
      ],
    },
    {
      label: "Categories",
      href: "/categories",
      subcategories: [
        { label: "Electronics", href: "/categories/electronics" },
        { label: "Clothing", href: "/categories/clothing" },
        { label: "Home & Garden", href: "/categories/home" },
      ],
    },
    { label: "Deals", href: "/deals" },
  ],
  cartCount = 0,
  wishlistCount = 0,
  onSearch,
  className,
}: NavbarEcommerceProps) {
  const [mobileMenuOpen, setMobileMenuOpen] = React.useState(false);
  const [searchQuery, setSearchQuery] = React.useState("");

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    onSearch?.(searchQuery);
  };

  return (
    <nav
      className={cn(
        "w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60",
        className
      )}
    >
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 items-center justify-between gap-4">
          {/* Logo */}
          <div className="flex-shrink-0">
            <Link href="/" className="flex items-center">
              {logo}
            </Link>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex lg:items-center lg:gap-6">
            {categories.map((category) =>
              category.subcategories ? (
                <DropdownMenu key={category.href}>
                  <DropdownMenuTrigger asChild>
                    <Button
                      variant="ghost"
                      className="flex items-center gap-1 text-sm font-medium text-muted-foreground hover:text-foreground"
                    >
                      {category.label}
                      <ChevronDown className="size-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="start">
                    {category.subcategories.map((sub) => (
                      <DropdownMenuItem key={sub.href} asChild>
                        <Link href={sub.href}>{sub.label}</Link>
                      </DropdownMenuItem>
                    ))}
                  </DropdownMenuContent>
                </DropdownMenu>
              ) : (
                <Link
                  key={category.href}
                  href={category.href}
                  className="text-sm font-medium text-muted-foreground transition-colors hover:text-foreground"
                >
                  {category.label}
                </Link>
              )
            )}
          </div>

          {/* Search Bar */}
          <form
            onSubmit={handleSearch}
            className="hidden flex-1 max-w-md md:flex"
          >
            <div className="relative w-full">
              <Search className="absolute left-3 top-1/2 size-4 -translate-y-1/2 text-muted-foreground" />
              <input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="h-10 w-full rounded-full border bg-muted/50 pl-10 pr-4 text-sm outline-none transition-colors focus:border-primary focus:bg-background"
              />
            </div>
          </form>

          {/* Actions */}
          <div className="flex items-center gap-2">
            <Button variant="ghost" size="icon" className="hidden sm:flex">
              <Search className="size-5 md:hidden" />
              <span className="sr-only">Search</span>
            </Button>

            <Button variant="ghost" size="icon" className="relative">
              <Heart className="size-5" />
              {wishlistCount > 0 && (
                <Badge
                  variant="destructive"
                  className="absolute -right-1 -top-1 size-5 rounded-full p-0 text-xs"
                >
                  {wishlistCount}
                </Badge>
              )}
              <span className="sr-only">Wishlist</span>
            </Button>

            <Button variant="ghost" size="icon" className="relative">
              <ShoppingCart className="size-5" />
              {cartCount > 0 && (
                <Badge
                  variant="default"
                  className="absolute -right-1 -top-1 size-5 rounded-full p-0 text-xs"
                >
                  {cartCount}
                </Badge>
              )}
              <span className="sr-only">Cart</span>
            </Button>

            <Button variant="ghost" size="icon" className="hidden sm:flex">
              <User className="size-5" />
              <span className="sr-only">Account</span>
            </Button>

            {/* Mobile menu button */}
            <Button
              variant="ghost"
              size="icon"
              className="lg:hidden"
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              aria-label="Toggle menu"
            >
              {mobileMenuOpen ? (
                <X className="size-5" />
              ) : (
                <Menu className="size-5" />
              )}
            </Button>
          </div>
        </div>

        {/* Mobile Search */}
        <div className="pb-3 md:hidden">
          <form onSubmit={handleSearch}>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 size-4 -translate-y-1/2 text-muted-foreground" />
              <input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="h-10 w-full rounded-full border bg-muted/50 pl-10 pr-4 text-sm outline-none transition-colors focus:border-primary focus:bg-background"
              />
            </div>
          </form>
        </div>
      </div>

      {/* Mobile Navigation */}
      {mobileMenuOpen && (
        <div className="border-t lg:hidden">
          <div className="space-y-1 px-4 pb-3 pt-2">
            {categories.map((category) => (
              <div key={category.href}>
                <Link
                  href={category.href}
                  className="block rounded-md px-3 py-2 text-base font-medium text-foreground"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  {category.label}
                </Link>
                {category.subcategories && (
                  <div className="ml-4 space-y-1">
                    {category.subcategories.map((sub) => (
                      <Link
                        key={sub.href}
                        href={sub.href}
                        className="block rounded-md px-3 py-2 text-sm text-muted-foreground hover:bg-accent"
                        onClick={() => setMobileMenuOpen(false)}
                      >
                        {sub.label}
                      </Link>
                    ))}
                  </div>
                )}
              </div>
            ))}
            <div className="mt-4 border-t pt-4">
              <Link
                href="/account"
                className="flex items-center gap-2 rounded-md px-3 py-2 text-base font-medium text-muted-foreground hover:bg-accent"
                onClick={() => setMobileMenuOpen(false)}
              >
                <User className="size-5" />
                My Account
              </Link>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
}
