import AccordionSeparated from "@/registry/ruixenui/accordion-separated";

const items = [
  {
    id: "1",
    title: "How do I create an account?",
    content:
      "Click the 'Sign Up' button in the top right corner. Enter your email and create a password to get started.",
  },
  {
    id: "2",
    title: "Can I have multiple accounts?",
    content:
      "Each user is allowed one account. Multiple accounts may be suspended without notice.",
  },
  {
    id: "3",
    title: "How do I reset my password?",
    content:
      "Click 'Forgot Password' on the login page. We'll send you a reset link to your registered email.",
  },
];

export default function AccordionSeparatedDemo() {
  return (
    <div className="flex min-h-[350px] w-full items-center justify-center p-4">
      <div className="w-full max-w-md">
        <AccordionSeparated items={items} defaultValue="1" />
      </div>
    </div>
  );
}
