"use client";

import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";

export interface AccordionSeparatedItem {
  id: string;
  title: string;
  content: string;
}

interface AccordionSeparatedProps {
  items: AccordionSeparatedItem[];
  defaultValue?: string;
  className?: string;
}

export default function AccordionSeparated({
  items,
  defaultValue,
  className,
}: AccordionSeparatedProps) {
  return (
    <Accordion
      type="single"
      collapsible
      defaultValue={defaultValue}
      className={className}
    >
      {items.map((item) => (
        <AccordionItem
          key={item.id}
          value={item.id}
          className="mb-3 last:mb-0 border-none rounded-lg bg-muted/40 px-4 data-[state=open]:bg-muted"
        >
          <AccordionTrigger className="text-left text-[15px] hover:no-underline py-4">
            {item.title}
          </AccordionTrigger>
          <AccordionContent className="text-muted-foreground pb-4">
            {item.content}
          </AccordionContent>
        </AccordionItem>
      ))}
    </Accordion>
  );
}
