"use client";

import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/components/ui/avatar";
import { cn } from "@/lib/utils";

interface AvatarOfflineProps {
  src?: string;
  alt?: string;
  fallback?: string;
  className?: string;
}

export default function AvatarOffline({
  src = "https://github.com/shadcn.png",
  alt = "User",
  fallback = "U",
  className,
}: AvatarOfflineProps) {
  return (
    <div className={cn("relative", className)}>
      <Avatar className="size-10 rounded-lg">
        <AvatarImage src={src} alt={alt} />
        <AvatarFallback className="rounded-lg">{fallback}</AvatarFallback>
      </Avatar>
      <span className="border-background bg-muted-foreground absolute bottom-0 end-0 size-3 rounded-full border-2">
        <span className="sr-only">Offline</span>
      </span>
    </div>
  );
}
