"use client";

import * as React from "react";
import Link from "next/link";
import { cn } from "@/lib/utils";
import { Menu, X, MoveRight } from "lucide-react";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
} from "@/components/ui/navigation-menu";

/* ── types ───────────────────────────────────────────────────── */

interface FeatureItem {
  title: string;
  description: string;
  icon: React.ReactNode;
  href: string;
}

interface ContentItem {
  title: string;
  icon: React.ReactNode;
  href: string;
}

interface FeaturesDropdown {
  type: "features";
  label: string;
  sectionTitle: string;
  items: FeatureItem[];
  footerLink?: { label: string; href: string };
}

interface SplitDropdown {
  type: "split";
  label: string;
  primary: { title: string; items: FeatureItem[] };
  secondary: { title: string; items: ContentItem[] };
}

interface NavLink {
  type: "link";
  label: string;
  href: string;
}

type NavItem = FeaturesDropdown | SplitDropdown | NavLink;

export interface NavbarDropdownProps {
  logo: React.ReactNode;
  items: NavItem[];
  primaryAction?: { href: string; label: string };
  secondaryAction?: { href: string; label: string };
  className?: string;
}

/* ── component ───────────────────────────────────────────────── */

export function NavbarDropdown({
  logo,
  items,
  primaryAction,
  secondaryAction,
  className,
}: NavbarDropdownProps) {
  const [menuOpen, setMenuOpen] = React.useState(false);

  return (
    <header
      className={cn("relative", className)}
      data-state={menuOpen ? "active" : "inactive"}
    >
      <div className="border-b bg-background/80 backdrop-blur">
        <div className="mx-auto max-w-6xl px-6 lg:px-12">
          <div className="relative flex flex-wrap items-center justify-between lg:py-4">
            <div className="flex justify-between gap-8 max-lg:h-14 max-lg:w-full max-lg:border-b">
              {/* logo */}
              <Link
                aria-label="home"
                className="flex items-center space-x-2"
                href="/"
              >
                {logo}
              </Link>

              {/* ── desktop navigation ──────────────────── */}
              <NavigationMenu className="max-lg:hidden">
                <NavigationMenuList className="gap-2">
                  {items.map((item, idx) => {
                    /* plain link */
                    if (item.type === "link") {
                      return (
                        <NavigationMenuItem key={idx}>
                          <NavigationMenuLink asChild>
                            <Link
                              href={item.href}
                              className="group inline-flex h-9 w-max items-center justify-center rounded-md px-4 py-2 text-sm font-medium text-muted-foreground transition-colors hover:bg-foreground/5 hover:text-foreground"
                            >
                              {item.label}
                            </Link>
                          </NavigationMenuLink>
                        </NavigationMenuItem>
                      );
                    }

                    /* single-column features dropdown */
                    if (item.type === "features") {
                      return (
                        <NavigationMenuItem key={idx}>
                          <NavigationMenuTrigger className="text-sm font-medium text-muted-foreground bg-transparent hover:bg-foreground/5 hover:text-foreground data-[state=open]:bg-foreground/5">
                            {item.label}
                          </NavigationMenuTrigger>
                          <NavigationMenuContent className="!w-[480px] p-4">
                            <p className="mb-3 px-2 text-xs font-semibold uppercase tracking-wider text-muted-foreground">
                              {item.sectionTitle}
                            </p>
                            <div className="flex flex-col gap-0.5">
                              {item.items.map((feature) => (
                                <NavigationMenuLink
                                  asChild
                                  key={feature.title}
                                >
                                  <Link
                                    href={feature.href}
                                    className="flex items-center gap-3 rounded-lg px-2 py-2.5 transition-colors hover:bg-muted"
                                  >
                                    <div className="flex size-10 shrink-0 items-center justify-center rounded-lg border border-border bg-background [&>svg]:size-5 [&>svg]:text-foreground">
                                      {feature.icon}
                                    </div>
                                    <div>
                                      <p className="text-sm font-medium text-foreground">
                                        {feature.title}
                                      </p>
                                      <p className="text-xs text-muted-foreground">
                                        {feature.description}
                                      </p>
                                    </div>
                                  </Link>
                                </NavigationMenuLink>
                              ))}
                            </div>
                            {item.footerLink && (
                              <div className="mt-3 px-2">
                                <Link
                                  href={item.footerLink.href}
                                  className="text-sm font-medium text-primary hover:underline"
                                >
                                  {item.footerLink.label}
                                </Link>
                              </div>
                            )}
                          </NavigationMenuContent>
                        </NavigationMenuItem>
                      );
                    }

                    /* two-column split dropdown */
                    if (item.type === "split") {
                      return (
                        <NavigationMenuItem key={idx}>
                          <NavigationMenuTrigger className="text-sm font-medium text-muted-foreground bg-transparent hover:bg-foreground/5 hover:text-foreground data-[state=open]:bg-foreground/5">
                            {item.label}
                          </NavigationMenuTrigger>
                          <NavigationMenuContent className="!w-[580px] p-4">
                            <div className="grid grid-cols-[1fr_0.75fr] gap-6">
                              {/* primary column */}
                              <div>
                                <p className="mb-3 px-2 text-xs font-semibold uppercase tracking-wider text-muted-foreground">
                                  {item.primary.title}
                                </p>
                                <div className="flex flex-col gap-0.5">
                                  {item.primary.items.map((feature) => (
                                    <NavigationMenuLink
                                      asChild
                                      key={feature.title}
                                    >
                                      <Link
                                        href={feature.href}
                                        className="flex items-center gap-3 rounded-lg px-2 py-2.5 transition-colors hover:bg-muted"
                                      >
                                        <div className="flex size-10 shrink-0 items-center justify-center rounded-lg border border-border bg-background [&>svg]:size-5 [&>svg]:text-foreground">
                                          {feature.icon}
                                        </div>
                                        <div>
                                          <p className="text-sm font-medium text-foreground">
                                            {feature.title}
                                          </p>
                                          <p className="text-xs text-muted-foreground">
                                            {feature.description}
                                          </p>
                                        </div>
                                      </Link>
                                    </NavigationMenuLink>
                                  ))}
                                </div>
                              </div>
                              {/* secondary column */}
                              <div>
                                <p className="mb-3 px-2 text-xs font-semibold uppercase tracking-wider text-muted-foreground">
                                  {item.secondary.title}
                                </p>
                                <div className="flex flex-col gap-0.5">
                                  {item.secondary.items.map((content) => (
                                    <NavigationMenuLink
                                      asChild
                                      key={content.title}
                                    >
                                      <Link
                                        href={content.href}
                                        className="flex items-center gap-3 rounded-lg px-2 py-2.5 transition-colors hover:bg-muted"
                                      >
                                        <span className="[&>svg]:size-5 [&>svg]:text-muted-foreground">
                                          {content.icon}
                                        </span>
                                        <p className="text-sm font-medium text-foreground">
                                          {content.title}
                                        </p>
                                      </Link>
                                    </NavigationMenuLink>
                                  ))}
                                </div>
                              </div>
                            </div>
                          </NavigationMenuContent>
                        </NavigationMenuItem>
                      );
                    }

                    return null;
                  })}
                </NavigationMenuList>
              </NavigationMenu>

              {/* mobile toggle */}
              <button
                aria-label={menuOpen ? "Close Menu" : "Open Menu"}
                onClick={() => setMenuOpen(!menuOpen)}
                className="relative z-20 -m-2.5 -mr-3 block cursor-pointer p-2.5 lg:hidden"
              >
                {menuOpen ? (
                  <X className="size-5" />
                ) : (
                  <Menu className="size-5" />
                )}
              </button>
            </div>

            {/* ── right-side CTA buttons (desktop) ─────── */}
            <div className="hidden lg:flex lg:items-center lg:gap-3">
              {secondaryAction && (
                <Link
                  href={secondaryAction.href}
                  className="inline-flex h-8 items-center justify-center rounded-md border border-transparent bg-card px-3 text-xs font-medium shadow-sm shadow-black/15 ring-1 ring-foreground/10 transition-colors duration-200 hover:bg-muted/50 dark:ring-foreground/15 dark:hover:bg-muted/50"
                >
                  {secondaryAction.label}
                </Link>
              )}
              {primaryAction && (
                <Link
                  href={primaryAction.href}
                  className="inline-flex h-8 items-center justify-center rounded-md border-[0.5px] border-white/10 bg-primary px-3 text-xs font-medium text-primary-foreground shadow-md shadow-black/15 ring-1 ring-[--ring-color] transition-colors hover:bg-primary/90 [--ring-color:color-mix(in_oklab,black_15%,hsl(var(--primary)))] dark:border-transparent dark:[--ring-color:color-mix(in_oklab,white_15%,hsl(var(--primary)))]"
                >
                  {primaryAction.label}
                </Link>
              )}
            </div>

            {/* ── mobile menu ──────────────────────────── */}
            {menuOpen && (
              <div className="absolute left-0 right-0 top-16 flex flex-col gap-6 border-t bg-background px-6 py-6 shadow-lg lg:hidden">
                {items.map((item, idx) => {
                  if (item.type === "link") {
                    return (
                      <Link
                        key={idx}
                        href={item.href}
                        className="text-sm font-medium"
                      >
                        {item.label}
                      </Link>
                    );
                  }

                  if (item.type === "features") {
                    return (
                      <div key={idx} className="flex flex-col gap-4">
                        <p className="text-xs font-semibold uppercase tracking-wider text-muted-foreground">
                          {item.sectionTitle}
                        </p>
                        {item.items.map((feature) => (
                          <Link
                            key={feature.title}
                            href={feature.href}
                            className="flex items-center justify-between"
                          >
                            <span className="text-sm">{feature.title}</span>
                            <MoveRight className="size-4 text-muted-foreground" />
                          </Link>
                        ))}
                      </div>
                    );
                  }

                  if (item.type === "split") {
                    return (
                      <div key={idx} className="flex flex-col gap-4">
                        <p className="text-xs font-semibold uppercase tracking-wider text-muted-foreground">
                          {item.primary.title}
                        </p>
                        {item.primary.items.map((feature) => (
                          <Link
                            key={feature.title}
                            href={feature.href}
                            className="flex items-center justify-between"
                          >
                            <span className="text-sm">{feature.title}</span>
                            <MoveRight className="size-4 text-muted-foreground" />
                          </Link>
                        ))}
                      </div>
                    );
                  }

                  return null;
                })}

                {(primaryAction || secondaryAction) && (
                  <div className="flex flex-col gap-3 border-t pt-4 sm:flex-row">
                    {secondaryAction && (
                      <Link
                        href={secondaryAction.href}
                        className="inline-flex h-8 items-center justify-center rounded-md border border-transparent bg-card px-3 text-xs font-medium shadow-sm shadow-black/15 ring-1 ring-foreground/10 transition-colors duration-200 hover:bg-muted/50 dark:ring-foreground/15 dark:hover:bg-muted/50"
                      >
                        {secondaryAction.label}
                      </Link>
                    )}
                    {primaryAction && (
                      <Link
                        href={primaryAction.href}
                        className="inline-flex h-8 items-center justify-center rounded-md border-[0.5px] border-white/10 bg-primary px-3 text-xs font-medium text-primary-foreground shadow-md shadow-black/15 ring-1 ring-[--ring-color] transition-colors hover:bg-primary/90 [--ring-color:color-mix(in_oklab,black_15%,hsl(var(--primary)))] dark:border-transparent dark:[--ring-color:color-mix(in_oklab,white_15%,hsl(var(--primary)))]"
                      >
                        {primaryAction.label}
                      </Link>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}
