"use client";

import { useState, useRef, useEffect } from "react";
import { motion } from "motion/react";

/**
 * Wordmark Footer — Rauno Freiberg craft, Ruixen UI edition.
 *
 * Giant half-cut wordmark on a full-width dark surface. The brand
 * text is enormous — stretching toward the viewport edges. A hairline
 * divides bright from dim; below the line the luminance plummets
 * and the container clips what remains. No mirror, no sound.
 * The scale IS the statement.
 */

/* ── Types ── */

interface WordmarkFooterProps {
  brandName?: string;
}

/* ── Scoped CSS ── */

const STYLE = `
.wf{
  --wf-bg:#161618;
  --wf-line:rgba(255,255,255,.07)
}
.dark .wf,[data-theme="dark"] .wf{
  --wf-bg:#111113;
  --wf-line:rgba(255,255,255,.08)
}
`.replace(/\n/g, "");

/* ── Gradient shared by triangle + text ── */

const GRAD =
  "linear-gradient(to bottom, rgba(255,255,255,.64) 0%, rgba(255,255,255,.52) 42%, rgba(255,255,255,.18) 62%, rgba(255,255,255,.04) 88%)";

/* ── Main component ── */

export function WordmarkFooter({
  brandName = "Ruixen UI",
}: WordmarkFooterProps) {
  const [inView, setInView] = useState(false);
  const ref = useRef<HTMLElement>(null);

  useEffect(() => {
    const el = ref.current;
    if (!el || typeof IntersectionObserver === "undefined") {
      setInView(true);
      return;
    }
    const obs = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setInView(true);
          obs.disconnect();
        }
      },
      { threshold: 0.1 },
    );
    obs.observe(el);
    return () => obs.disconnect();
  }, []);

  return (
    <section
      ref={ref}
      className="wf"
      style={{
        position: "relative",
        width: "100%",
        overflow: "hidden",
        background: "var(--wf-bg)",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      }}
    >
      <style dangerouslySetInnerHTML={{ __html: STYLE }} />

      {/* ── Wordmark ── */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={inView ? { opacity: 1, y: 0 } : {}}
        transition={{ type: "spring", stiffness: 260, damping: 28 }}
        style={{
          display: "flex",
          alignItems: "center",
          gap: "clamp(10px, 2vw, 28px)",
          padding:
            "clamp(32px, 4vw, 56px) clamp(24px, 4vw, 56px) clamp(8px, 1.5vw, 20px)",
        }}
      >
        {/* Triangle mark — CSS clip-path with gradient fill */}
        <div
          style={{
            width: "clamp(50px, 10vw, 140px)",
            height: "clamp(44px, 9vw, 124px)",
            background: GRAD,
            clipPath: "polygon(50% 0%, 100% 100%, 0% 100%)",
            flexShrink: 0,
          }}
        />

        {/* Brand text */}
        <span
          style={{
            fontSize: "clamp(56px, 14vw, 200px)",
            fontWeight: 700,
            letterSpacing: "-0.05em",
            lineHeight: 1,
            background: GRAD,
            WebkitBackgroundClip: "text",
            backgroundClip: "text",
            WebkitTextFillColor: "transparent",
            userSelect: "none",
            whiteSpace: "nowrap",
          }}
        >
          {brandName}
        </span>
      </motion.div>

      {/* ── Hairline divider at the midpoint ── */}
      <div
        style={{
          position: "absolute",
          left: 0,
          right: 0,
          top: "55%",
          height: 0.5,
          background: "var(--wf-line)",
          pointerEvents: "none",
        }}
      />
    </section>
  );
}

export default WordmarkFooter;
